  props: {
    media: Object,
  },

  data() {
    return {
      thumbnail: 'data:image/jpeg;base64,',
      episodes: [],
    }
  },

  async created() {
    this.episodes = await this.$store.dispatch('episode/getMediaEpisodesById', this.media.id)

    console.log(this.episodes)
  },

  components: {Popup, Exit, SeriesItem}
}
</script>

